import React from 'react';

class App extends React.Component {
  state = {
    counter: 0,
  }

  onClick() {
    const { counter } = this.state;
    this.setState({ counter: counter + 1 });
  }

  render() {
    const { counter } = this.state;
    return (
      <div>
        <h1>HELLO!</h1>
        <p>{counter}</p>
        <button onClick={this.onClick.bind(this)}>Increment</button>
      </div>
    );
  }
}

export default App;
